import React from 'react'

export default function Recipe(props) {
    console.log(props);
    let recipe = props.data.find(item => item.recipe.label===  props.match.params.name)

    console.log(recipe);
    return (
        <div>
            <h2>{props.state.recipe.label}</h2>
            <img src={recipe.recipe.image} />
        </div>
    )
}
