import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import wrapper from './components/Wrapper';
import Wrapper from './components/Wrapper';

export default class App extends Component {

  constructor(props){
    super(props)
    this.state={
      data:[],
      searchValue:"chicken"
    }
    console.log("constructor");
  }

  // react will execute this code just before teh render method
  static getDerivedStateFromProps(){
    console.log("getDerivedStateFromProps");
    return null

  }
// when the user start app for the fist time , this data is fetch and display on the screen just once
  componentDidMount(){
    console.log("componentDidMount")
    fetch(`https://api.edamam.com/search?q=${this.state.searchValue}&app_id=${process.env.REACT_APP_ID}&app_key=${process.env.REACT_APP_API_KEY}`)
    .then(res=>res.json())
    .then(result =>{console.log(result)
    this.setState({data:result.hits})})
  }

  searchData=(e)=>{
    e.preventDefault()
    fetch(`https://api.edamam.com/search?q=${this.state.searchValue}&app_id=${process.env.REACT_APP_ID}&app_key=${process.env.REACT_APP_API_KEY}`)
    .then(res=>res.json())
    .then(result =>{console.log(result)
    this.setState({data:result.hits})})
  }

  shouldComponentUpdate(){
    console.log("shouldComponentUpdate")
    return true
  }

  render() {
    console.log("render");
    return (
       <div>
        <div className="container bg-dark text-white">
          <h1>Recipe App</h1>
         <form onSubmit={this.searchData} >
           <label>Search Recipe: <input type="text" onChange={(e)=>this.setState({searchValue:e.target.value})} /> </label>
           <input type="submit" value="search"/>
         </form>
        </div>
          <Wrapper data={this.state.data} />
      </div>
    )
  }
}
